let numberId = 0;
const intialState = {
  todos: []
}
function todoReducer(state = intialState, action, text) {
  switch (action.type) {
    case 'task/add':
      return {
        ...state,
        todos
      }

    case 'task/remove':
      return {}

    default:
      return state
  }
}

export { todoReducer };
