let numberId = 0;
const intialState = {
  todos: []
}
function todoReducer(state = intialState, action) {
  switch (action.type) {
    case 'task/addTask':
      return {
        ...state,
        todos: [...state.todos, {
          id: ++numberId,
          title: action.payload.title,
          completed: false,
        }]
      }

    case 'task/remove':
      return {}

    default:
      return state
  }
}

export { todoReducer };
