import React from "react";

import { useDispatch, useSelector } from "react-redux";

import { removeTask, toggleTask } from "../store/actions/todo";

const ToDoList = () => {

  const tasks = useSelector(state => state.toDo.todos);

  const dispatch = useDispatch();

  // const handleRemoveTask = (id) => () => {
  //   dispatch(removeTask(id));
  // }

  // const handleToggleTask = (id) => () => {
  //   dispatch(toggleTask(id))
  // }

  console.log('tasks', tasks)

  return (
    <div className="task-list">
      {

        tasks?.map((task) => (

          <div key={task.id} task={task.title} style={{ textDecoration: task.completed ? "line-through" : "none" }} >
            {task.title}
            {/* 
              <button onClick={handleRemoveTask(task.id)}>Remove</button>
              <button onClick={handleToggleTask(task.id)}>Done</button> */}

          </div>
        ))
      }
    </div>
  )


}

export default ToDoList;
