/* eslint-disable no-sequences */
import { useDispatch, useSelector } from 'react-redux';
import { addTask } from '../../store/actions';


import { useState } from "react";

function InputList() {
  const [inputValue, setInputValue] = useState("");
  const store = useSelector((store) => store);
  const dispatch = useDispatch();

  const inputState = useSelector(state => {
    return state.value;
  });
  const addTasks = () => {
    dispatch(addTask())
  }
  function showInput(event) {
    setInputValue(event.target.value);

  }

  // state = {
  //   task: '',
  // }
  // handleInputChange = ({ target: { value } }) => {
  //   this.setState({
  //     task: value,
  //   })
  // }
  dispatch(addTask())
  const textVal = useRef();
  function sendTextValueHandler() {
    const inputVal = textVal.current.value
    dispatch({ type: 'INP_VAL', message: inputVal }) //Pass input value to redux store
  }
  return (
    <div>
      <form onSubmit={e => e.preventDefault()} >
        <input type='text' ref={textVal}  ></input>;
        <button onClick={sendTextValueHandler} > press</button>
      </form>
      {inputState}
    </div>
  )
}
export default InputList;
